import { Component, Input } from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { MatDividerModule } from '@angular/material/divider';
import { NgxChartsModule } from '@swimlane/ngx-charts';
import { PIE_CHART_CONFIG } from './configs/pie-chart.config';

import { CommonModule } from '@angular/common';
import { PackageType } from 'src/app/core/models/package';

@Component({
  selector: 'app-package',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatDividerModule, NgxChartsModule],
  templateUrl: './package.component.html',
  styleUrls: ['./package.component.css'],
})
export class PackageComponent {
  @Input() PackageData!: {
    packageType: PackageType;
    packageName: string;
    packageUsage: {
      used: number;
      total: number;
    };
  };

  used = Math.round(Math.random() * 100);
  // pie chart
  PIE_CHART_CONFIG = PIE_CHART_CONFIG;
  single = [
    {
      name: 'Used',
      value: this.used,
    },
    {
      name: 'Remaining',
      value: 100 - this.used,
    },
  ];

  constructor() {}

  // can be used later
  onSelect(data: any): void {
    console.log('Item clicked', JSON.parse(JSON.stringify(data)));
  }

  onActivate(data: any): void {
    console.log('Activate', JSON.parse(JSON.stringify(data)));
  }

  onDeactivate(data: any): void {
    console.log('Deactivate', JSON.parse(JSON.stringify(data)));
  }
  //
}
<mat-card class="package">
  <mat-card-header>
    <mat-card-subtitle>Package Type</mat-card-subtitle>
    <mat-card-title class="title"
      >Package Name Package Name Package Name</mat-card-title
    >
  </mat-card-header>
  <mat-card-content class="package-content">
    <p>Used: {{ used }}/100 ({{ used }}%)</p>
    <ngx-charts-pie-chart
      class="pie-chart"
      [view]="PIE_CHART_CONFIG.view"
      [results]="single"
      [customColors]="PIE_CHART_CONFIG.packageColorsByType[0]"
      [doughnut]="PIE_CHART_CONFIG.isDoughnut"
      [arcWidth]="PIE_CHART_CONFIG.arcWidth"
      (select)="onSelect($event)"
      (activate)="onActivate($event)"
      (deactivate)="onDeactivate($event)"
    >
    </ngx-charts-pie-chart>
  </mat-card-content>
</mat-card>
