import { Component } from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { MatChipsModule } from '@angular/material/chips';
import { MatDividerModule } from '@angular/material/divider';
import { MatIconModule } from '@angular/material/icon';
import { NgxChartsModule } from '@swimlane/ngx-charts';
import { PIE_CHART_CONFIG } from './pie-chart.config';

@Component({
  selector: 'app-package',
  standalone: true,
  imports: [
    MatCardModule,
    MatChipsModule,
    MatDividerModule,
    MatIconModule,
    NgxChartsModule,
  ],
  templateUrl: './package.component.html',
  styleUrls: ['./package.component.css'],
})
export class PackageComponent {
  // pie chart
  PIE_CHART_CONFIG = PIE_CHART_CONFIG;
  single = [
    {
      name: 'Used',
      value: 20,
    },
    {
      name: 'Remaining',
      value: 80,
    },
  ];

  constructor() {}

  onSelect(data: any): void {
    console.log('Item clicked', JSON.parse(JSON.stringify(data)));
  }

  onActivate(data: any): void {
    console.log('Activate', JSON.parse(JSON.stringify(data)));
  }

  onDeactivate(data: any): void {
    console.log('Deactivate', JSON.parse(JSON.stringify(data)));
  }
}
<mat-card class="package">
  <mat-card-header>
    <mat-card-subtitle
      ><mat-chip color="primary">
        <mat-icon
          aria-hidden="false"
          aria-label="Example home icon"
          fontIcon="home"
        ></mat-icon>

        Package Type
      </mat-chip></mat-card-subtitle
    >
    <mat-card-title>Package Name</mat-card-title>
  </mat-card-header>
  <mat-card-content class="package-content">
    <p>Usage: 15/100 (15%)</p>
    <ngx-charts-pie-chart
      [view]="PIE_CHART_CONFIG.view"
      [results]="single"
      [doughnut]="PIE_CHART_CONFIG.isDoughnut"
      [arcWidth]="PIE_CHART_CONFIG.arcWidth"
      (select)="onSelect($event)"
      (activate)="onActivate($event)"
      (deactivate)="onDeactivate($event)"
    >
    </ngx-charts-pie-chart>
  </mat-card-content>
</mat-card>
