import { Component, Inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatDialogModule, MatDialogRef } from '@angular/material/dialog';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-edit-user-details',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatDialogModule,
    MatButtonModule,
    MatInputModule,
    MatFormFieldModule,
  ],
  templateUrl: './edit-user-details.component.html',
  styleUrls: ['./edit-user-details.component.css'],
})
export class EditUserDetailsComponent implements OnInit {
  addressForm!: FormGroup;

  constructor(
    public dialogRef: MatDialogRef<EditUserDetailsComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit(): void {
    this.addressForm = this.formBuilder.group({
      city: ['', Validators.required],
      street: ['', Validators.required],
      homeNumber: ['', Validators.required],
      postalCode: ['', Validators.required],
    });
  }

  onSubmit(): void {
    if (this.addressForm.valid) {
      console.log('Form submitted:', this.addressForm.value);
      // TODO: submit form data to server
    }
  }

  onCancel(): void {
    console.log('Form cancelled');
    // TODO: handle form cancel event
  }
}
<form [formGroup]="addressForm" (ngSubmit)="onSubmit()">
  <mat-form-field appearance="fill">
    <mat-label>City</mat-label>
    <input matInput formControlName="city" required />
    <mat-error *ngIf="addressForm.controls['city'].invalid">
      City is required.
    </mat-error>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Street</mat-label>
    <input matInput formControlName="street" required />
    <mat-error *ngIf="addressForm.controls['street'].invalid">
      Street is required.
    </mat-error>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Home Number</mat-label>
    <input matInput formControlName="homeNumber" required />
    <mat-error *ngIf="addressForm.controls['homeNumber'].invalid">
      Home number is required.
    </mat-error>
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Postal Code</mat-label>
    <input matInput formControlName="postalCode" required />
    <mat-error *ngIf="addressForm.controls['postalCode'].invalid">
      Postal code is required.
    </mat-error>
  </mat-form-field>
  <div class="button-container">
    <button
      mat-raised-button
      color="primary"
      type="submit"
      [disabled]="addressForm.invalid"
    >
      Save
    </button>
    <button mat-button color="warn" type="button" (click)="onCancel()">
      Cancel
    </button>
  </div>
</form>
